import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "",
    email: "",
    phone: "",
    organization: "",
    address: "",
    state: "",
    zipCode: "",
    country: "",
    language: "",
    timeZones: "",
    currency: "",
    image: "",
  });

  const [selectedFile, setSelectedFile] = useState(null);

  // Fetch user data when component mounts
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/user/1"); // Assuming user id is 1
        setProfile(response.data);
      } catch (error) {
        console.error("Error fetching profile:", error);
      }
    };

    fetchProfile();
  }, []);

  // Handle form inputs
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  // Handle file input
  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  // Handle form submit to update user profile
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("name", profile.name);
    formData.append("email", profile.email);
    formData.append("phone", profile.phone);
    formData.append("organization", profile.organization);
    formData.append("address", profile.address);
    formData.append("state", profile.state);
    formData.append("zipCode", profile.zipCode);
    formData.append("country", profile.country);
    formData.append("language", profile.language);
    formData.append("timeZones", profile.timeZones);
    formData.append("currency", profile.currency);

    if (selectedFile) {
      formData.append("image", selectedFile);
    }

    try {
      await axios.post(`http://localhost:8000/api/user/1/update`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      alert("Profile updated successfully");
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  return (
    <div className="card-body">
      <div className="d-flex align-items-start align-items-sm-center gap-4 pb-4 border-bottom">
        <img
          src={`http://localhost:8000/storage/${profile.image}`}
          alt="user-avatar"
          className="d-block w-px-100 h-px-100 rounded"
          id="uploadedAvatar"
        />
        <div className="button-wrapper">
          <label
            htmlFor="upload"
            className="btn btn-primary me-3 mb-4"
            tabIndex="0"
          >
            <span className="d-none d-sm-block">Upload new photo</span>
            <i className="bx bx-upload d-block d-sm-none"></i>
            <input
              type="file"
              id="upload"
              className="account-file-input"
              hidden
              accept="image/png, image/jpeg"
              onChange={handleFileChange}
            />
          </label>
          <div>Allowed JPG, GIF or PNG. Max size of 800K</div>
        </div>
      </div>
      <div className="card-body pt-4">
        <form id="formAccountSettings" onSubmit={handleSubmit}>
          <div className="row g-4">
            <div className="col-md-6">
              <label htmlFor="name" className="form-label">
                Name
              </label>
              <input
                className="form-control"
                type="text"
                id="name"
                name="name"
                value={profile.name}
                onChange={handleInputChange}
                autoFocus
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="email" className="form-label">
                E-mail
              </label>
              <input
                className="form-control"
                type="text"
                id="email"
                name="email"
                value={profile.email}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="phone" className="form-label">
                Phone Number
              </label>
              <input
                className="form-control"
                type="text"
                id="phone"
                name="phone"
                value={profile.phone}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="organization" className="form-label">
                Organization
              </label>
              <input
                type="text"
                className="form-control"
                id="organization"
                name="organization"
                value={profile.organization}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="address" className="form-label">
                Address
              </label>
              <input
                type="text"
                className="form-control"
                id="address"
                name="address"
                value={profile.address}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="state" className="form-label">
                State
              </label>
              <input
                className="form-control"
                type="text"
                id="state"
                name="state"
                value={profile.state}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="zipCode" className="form-label">
                Zip Code
              </label>
              <input
                type="text"
                className="form-control"
                id="zipCode"
                name="zipCode"
                value={profile.zipCode}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="country" className="form-label">
                Country
              </label>
              <input
                className="form-control"
                type="text"
                id="country"
                name="country"
                value={profile.country}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="language" className="form-label">
                Language
              </label>
              <input
                className="form-control"
                type="text"
                id="language"
                name="language"
                value={profile.language}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="timeZones" className="form-label">
                Timezone
              </label>
              <input
                className="form-control"
                type="text"
                id="timeZones"
                name="timeZones"
                value={profile.timeZones}
                onChange={handleInputChange}
              />
            </div>
            <div className="col-md-6">
              <label htmlFor="currency" className="form-label">
                Currency
              </label>
              <input
                className="form-control"
                type="text"
                id="currency"
                name="currency"
                value={profile.currency}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="mt-4">
            <button type="submit" className="btn btn-primary me-3">
              Save changes
            </button>
            <button type="reset" className="btn btn-outline-secondary">
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Profile;
