import React, { useState, useEffect } from "react";
import axios from "axios";

const Subscription = () => {
  const [subscriptions, setSubscriptions] = useState([]);

  useEffect(() => {
    // Fetch all subscriptions with related mealClass and user
    axios
      .get("http://localhost:8000/api/admin/subscriptions")
      .then((response) => {
        setSubscriptions(response.data); // Set the fetched subscriptions
      })
      .catch((error) => {
        console.error("Error fetching subscriptions", error);
      });
  }, []);

  const handleStatusChange = (id, newStatus) => {
    const confirmChange = window.confirm(
      `Are you sure you want to ${newStatus} this subscription?`
    );

    if (confirmChange) {
      axios
        .post(`http://localhost:8000/api/admin/subscriptions/${id}/status`, {
          status: newStatus,
        })
        .then(() => {
          // Update the local state with the new status
          setSubscriptions((prevSubscriptions) =>
            prevSubscriptions.map((sub) =>
              sub.id === id ? { ...sub, status: newStatus } : sub
            )
          );
        })
        .catch((error) => {
          console.error("Error updating subscription status", error);
        });
    }
  };

  return (
    <table className="table">
      <thead>
        <tr>
          <th>id</th>
          <th>Subscriptions Class</th>
          <th>User Name</th>
          <th>Start Date</th>
          <th>End Date</th>
          <th>Status</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody className="table-border-bottom-0">
        {subscriptions.map((subscription) => (
          <tr key={subscription.id}>
            <td>{subscription.id}</td>
            <td>{subscription.mealClass.name}</td>{" "}
            {/* Display the meal class name */}
            <td>{subscription.user.name}</td> {/* Display the user name */}
            <td>{subscription.start_date}</td>
            <td>{subscription.end_date}</td>
            <td>
              <span
                className={`badge bg-label-${
                  subscription.status === "Active" ? "primary" : "secondary"
                } me-1`}
              >
                {subscription.status}
              </span>
            </td>
            <td>
              <button
                className="btn btn-primary me-2"
                onClick={() => handleStatusChange(subscription.id, "Activate")}
              >
                Activate
              </button>
              <button
                className="btn btn-success me-2"
                onClick={() => handleStatusChange(subscription.id, "Completed")}
              >
                Complete
              </button>
              <button
                className="btn btn-danger"
                onClick={() => handleStatusChange(subscription.id, "Cancelled")}
              >
                Cancel
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};
export default Subscription;
