import React, { useState, useEffect } from "react";
import axios from "axios";

const Subscription = () => {
  const [subscriptions, setSubscriptions] = useState([]);

  useEffect(() => {
    // Fetch all subscriptions with related mealClass and user
    axios
      .get("http://localhost:8000/api/admin/subscriptions")
      .then((response) => {
        setSubscriptions(response.data); // Set the fetched subscriptions
      })
      .catch((error) => {
        console.error("Error fetching subscriptions", error);
      });
  }, []);

  const handleStatusChange = (id, newStatus) => {
    const confirmChange = window.confirm(
      `Are you sure you want to ${newStatus} this subscription?`
    );

    if (confirmChange) {
      axios
        .post(`http://localhost:8000/api/admin/subscriptions/${id}/status`, {
          status: newStatus,
        })
        .then(() => {
          // Update the local state with the new status
          setSubscriptions((prevSubscriptions) =>
            prevSubscriptions.map((sub) =>
              sub.id === id ? { ...sub, status: newStatus } : sub
            )
          );
        })
        .catch((error) => {
          console.error("Error updating subscription status", error);
        });
    }
  };

  return (
    <div class="container-xxl flex-grow-1 container-p-y aseelContainer">
      <div className="card">
        <h5 className="card-header">Table Basic</h5>
        <div className="table-responsive text-nowrap">
          <table className="table">
            <thead>
              <tr>
                <th>id</th>
                <th>Subscriptions class</th>
                <th>User Name</th>
                <th>Start Date</th>
                <th>End Date</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody className="table-border-bottom-0">
              <tr>
                <td>1</td>
                <td>Albert Cook</td>
                <td>Angular Project</td>
                <td>xx/xx/xxxx</td>
                <td>xx/xx/xxxx</td>
                <td>
                  <span className="badge bg-label-primary me-1">Active</span>
                </td>
                <td>
                  <button>
                    <i className="bx bx-dots-vertical-rounded"></i>Approve
                  </button>
                  <div>
                    <button>
                      <i className="bx bx-edit-alt me-1"></i> Activate
                    </button>
                    <button>
                      <i className="bx bx-trash me-1"></i> End
                    </button>
                  </div>
                </td>
              </tr>

              {/* Add more rows for other projects as needed */}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Subscription;
